definition.script:
pipeline {
    agent any

    triggers {
        pollSCM('*/3 * * * *')
    }

    environment {
        DOCKER = credentials('docker-hub')
    }

    stages {
        stage('Prepare') {
            agent any

            steps {
                git url :'https://github.com/JINU-CHANG/2023-1-OSSPrac-Momentum-2',
                branch:'main',
                credentialsId: 'github'
            }
        }

        stage('Build') {
            steps {
                sh 'docker-compose build hw5'
            }
        }

        stage('Tag') {
            steps {
                script {
                    sh "docker tag ${DOCKER_USR}/momentum ${DOCKER_USR}/momentum:${BUILD_NUMBER}"
                }
            }
        }

        stage('Push') {
            steps {
                script {
                    sh "docker login -u ${DOCKER_USR} -p ${DOCKER_PWS}"
                    sh "docker push ${DOCKER_USR}/momentum:${BUILD_NUMBER}"
                }
            }
        }
    }
}
